using System;
using System.Collections;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading;
using System.Threading.Tasks;

namespace pdf.ServerTty
{
    public class Terminal
    {
        private string terminal_prompt = "server> ";
        private Int32 x = 125, y = 33;
        private byte b_key = 0xAB;
        private string banner = "";

        public Terminal() { Console.SetWindowSize(x, y); }
        public void Banner(int i) => xPrintLn(System.Text.Encoding.UTF8.GetString(Convert.FromBase64String(banner)), i);

        public void Print(string s) => Console.WriteLine(terminal_prompt + s);
        public void PrintLn(string s) => Console.WriteLine(terminal_prompt + s);
        public void xPrintLn(string s, int i) { foreach (var line in s.Split('\n')) { Console.WriteLine(line); Thread.Sleep(i); } }
        public string ReadLn() => Console.ReadLine();
    }
}
